import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NearbyPlacesCarousel from './NearbyPlacesCarousel';

const NearbyArticles = ({ lat, lon }) => {
  const [nearbyArticles, setNearbyArticles] = useState([]);
  const [title,setTitles] = useState([]);

  useEffect(() => {
    const fetchNearbyArticles = async () => {
        if(lat && lon){
            try {
                const response = await axios.get(
                `https://en.wikipedia.org/w/api.php`, {
                params: {
                    action: 'query',
                    list: 'geosearch',
                    gscoord: `${lat}|${lon}`,
                    gsradius: 1000,
                    gslimit: 10,
                    format: 'json',
                    origin: '*',
                },
                });
                debugger;
                setNearbyArticles(response.data.query.geosearch);
                for(let i=0;i<response.data.query.geosearch.length;i++){
                    setTitle(response.data.query.geosearch[i].title);
                }
            } catch (error) {
                console.error('Error fetching nearby articles:', error);
            }
        }
    };

    fetchNearbyArticles();
  }, [lat, lon]);

  return (
    <div>
      <h3>Nearby Places of Interest</h3>
      <ul>
        {nearbyArticles.map(article => (
          <li key={article.pageid}>
            <a 
              href={`https://en.wikipedia.org/?curid=${article.pageid}`} 
              target="_blank" 
              rel="noopener noreferrer">
              {article.title}
            </a>
          </li>
        ))}
      </ul>
      <NearbyPlacesCarousel places={title} />
    </div>
  );
};

export default NearbyArticles;
