import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import './chartSetup'; // Ensure the components are registered once

const TideInfo = ({ lat, lon }) => {
  const [tideData, setTideData] = useState([]);
  const [chartKey, setChartKey] = useState(0); // Key to force re-rendering

  useEffect(() => {
    const fetchTideData = async () => {
      try {
        const response = await axios.get('/tideData.json'); 
        const beaches = response.data;

        const beachData = beaches.find(beach => beach.lat === parseFloat(lat));
        if (beachData && beachData.data && beachData.data.heights) {
          setTideData(beachData.data.heights);
          setChartKey((prevKey) => prevKey + 1);
        } else {
          console.warn('No data found for the provided latitude.');
        }
      } catch (error) {
        console.error('Error fetching tide information:', error);
      }
    };

    fetchTideData();
  }, [lat, lon]);

  const formatDataForGraph = () => {
    const labels = tideData.map((data) => {
      const date = new Date(data.dt * 1000);
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    });

    const heights = tideData.map((data) => data.height);

    return {
      labels,
      datasets: [
        {
          label: 'Tide Height (ft)',
          data: heights,
          borderColor: '#4A90E2',
          backgroundColor: 'rgba(74, 144, 226, 0.3)',
          fill: true,
          tension: 0.4,
        },
      ],
    };
  };

  return (
    <div className="tide-info bg-gradient-to-r from-blue-300 to-blue-500 p-6 rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold mb-6 text-white">Tide Information</h2>

      {tideData.length > 0 && (
        <div className="mb-6">
          <Line key={chartKey} data={formatDataForGraph()} />
        </div>
      )}

      {tideData.length > 0 && (
        <div className="bg-white p-4 rounded-lg shadow-md">
          <table className="min-w-full bg-white rounded-lg shadow-md">
            <thead>
              <tr className="bg-blue-500 text-white">
                <th className="py-2 px-4 text-left">Time</th>
                <th className="py-2 px-4 text-left">Height (ft)</th>
              </tr>
            </thead>
            <tbody>
              {tideData.map((tide, index) => {
                const time = new Date(tide.dt * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
                return (
                  <tr key={index} className="border-b">
                    <td className="py-2 px-4">{time}</td>
                    <td className="py-2 px-4">{tide.height.toFixed(2)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default TideInfo;
